<script>
  import config from './config.json';
  import draggable from 'vuedraggable'
  let data = [];
  let products = [
      {
          id: 1,
          sort: 1,
          name: "Product Name",
          price: "8$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 2,
          sort: 1,
          name: "Product Name",
          price: "8$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 3,
          sort: 1,
          name: "Product Name",
          price: "8$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 4,
          sort: 1,
          name: "Product Name",
          price: "8$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 5,
          sort: 1,
          name: "Product Name",
          price: "8$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 6,
          sort: 1,
          name: "Product Name",
          price: "16$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 7,
          sort: 1,
          name: "Product Name",
          price: "16$",
          imageUrl: "data:image/jpeg;base64,"
      },
      {
          id: 8,
          sort: 1,
          name: "Product Name",
          price: "16$",
          imageUrl: "data:image/jpeg;base64,"
      }
  ];
  function getRandomArbitrary(min, max) {
      return Math.random() * (max - min) + min;
  }
  config.blocks.forEach(function(block){
      let newProducts = products;
      newProducts.forEach(function (product) {
        product.unique = Date.now() + getRandomArbitrary(0, 100000);
      });
      data.push({
          startProducts: newProducts
      });
  });
  export default{
      components: {
          draggable,
      },
    data(){
      return{
        config: config,
        activeConfig: config.blocks[0],
        activeBlockIndex: 0,
        data: data,
        drag: false,
        days: [
            "SUN",
            "MON",
            "TUE",
            "WED",
            "THU",
            "FRI",
            "SAT"
        ]
      }
    },
    methods: {
      activateBlock(index,config){
        this.activeConfig = config;
        this.activeBlockIndex = index;
      },
      checkProduct(product, event){
        if(event.target.checked){
            if(!this.data[this.activeBlockIndex].products){
                this.data[this.activeBlockIndex].products = [];
            }
            this.data[this.activeBlockIndex].products.push(product);
        }else{
            let index;
            this.data[this.activeBlockIndex].products.forEach(function(p, i){
                if(p.id === product.id){
                    index = i;
                }
            });
            this.data[this.activeBlockIndex].products.splice(index, 1);
        }
      },
      productChecked(productId){
        if(this.data[this.activeBlockIndex].products && this.data[this.activeBlockIndex].products.length > 0){
            let r = false;
            this.data[this.activeBlockIndex].products.forEach(function(p){
                if(p.id === productId){
                    r = true;
                }
            });
            return r;
        }else{
            return false;
        }
      },
      addOption(){
          if(!this.data[this.activeBlockIndex].options){
              this.data[this.activeBlockIndex].options = [];
          }
          this.data[this.activeBlockIndex].options.push({
              name: "",
              price: ""
          });
      },
      optionRemove(index){
          this.data[this.activeBlockIndex].options.splice(index, 1);
      },
      onEnd(){
          this.drag = false;
          let products = this.data[this.activeBlockIndex].products;
          this.data[this.activeBlockIndex].products = [];
          this.data[this.activeBlockIndex].startProducts.forEach((product) => {
              products.forEach((p) => {
                  if(p.id === product.id){
                      this.data[this.activeBlockIndex].products.push(product);
                  }
              });
          });
          console.log(this.data);
      },
      activateShowTime(index){
          let active = this.data[this.activeBlockIndex].startProducts[index].showtime;
          if(active){
              this.data[this.activeBlockIndex].startProducts[index].showtime = false;
          }else{
              this.data[this.activeBlockIndex].startProducts[index].showtime = true;
          }
      },
      activateDay(day, index){
        if(!this.data[this.activeBlockIndex].startProducts[index].days){
            this.data[this.activeBlockIndex].startProducts[index].days = [];
        }
        console.log(this.data[this.activeBlockIndex].startProducts[index].days);
        let indexOf = this.data[this.activeBlockIndex].startProducts[index].days.indexOf(day);
        if(indexOf > -1){
            this.data[this.activeBlockIndex].startProducts[index].days.splice(indexOf, 1);
        }else{
            this.data[this.activeBlockIndex].startProducts[index].days.push(day);
        }
      },
      checkDay(day, index){
          if(this.data[this.activeBlockIndex].startProducts[index].days){
              if(this.data[this.activeBlockIndex].startProducts[index].days.indexOf(day) > -1){
                  return true;
              }
          }
          return false;
      }
    }
  }
</script>

<template>
  <div class="menu-board-container">
    <div class="mbc-column">
      <h3>Blocks</h3>
      <div class="blocks-tabs">
        <div @click="activateBlock(index, block)" v-for="(block, index) in config.blocks" :class="{ active: index === activeBlockIndex, blockTab: true }">
          {{block.title}}
        </div>
      </div>

      <div class="options-container">
          <div v-if="activeConfig.grid" class="option grid-option">
              <p>Grid</p>
              <input type="text" v-model="data[activeBlockIndex].columns">
              <p>x</p>
              <input type="text" v-model="data[activeBlockIndex].rows">
          </div>
          <div v-if="activeConfig.header1" class="option header-option">
              <input type="text" v-model="data[activeBlockIndex].header1">
          </div>

          <div v-if="activeConfig.options" class="option opts">
              <div class="opt" v-for="(option, index) in data[activeBlockIndex].options">
                  <input :placeholder="'Option ' + (index + 1)" type="text" v-model="data[activeBlockIndex].options[index].name">
                  <div>
                      <span>$</span>
                      <input type="text" v-model="data[activeBlockIndex].options[index].price">
                  </div>
                  <div class="opt-remove" @click="optionRemove(index)"></div>
              </div>
              <button class="add-option" @click="addOption">
                  <i class="add-icon"></i>
                  <span>add option</span>
              </button>
          </div>
      </div>

      <div class="blocks-container">
        <div @click="activateBlock(index, block)" v-for="(block, index) in config.blocks" :class="{active: index === activeBlockIndex, block: true}" :style="{'grid-column': block['grid-column'], 'grid-row': block['grid-row']}"></div>
      </div>
    </div>
    <div class="mbc-column">
        <div v-if="activeConfig.products">
            <h3>Products</h3>
            <perfect-scrollbar class="products-container">
                <draggable v-model="data[activeBlockIndex].startProducts" item-key="unique" @start="drag=true" @end="onEnd">
                    <template #item="{element, index}">
                        <div class="product" :class="{active: productChecked(element.id)}" :key="element.unique">
                            <div class="pleft">
                                <div class="product-checkbox">
                                    <label>
                                        <input :checked="productChecked(element.id)" type="checkbox" @change="(event) => checkProduct(element,event)">
                                    </label>
                                </div>
                                <div class="product-image">
                                    <img :src="element.imageUrl" alt="">
                                </div>
                                <div class="product-price">
                                    {{ element.price }}
                                </div>
                                <div class="product-name">
                                    {{ element.name }}
                                </div>
                            </div>
                            <div class="pright">
                                <div class="showtime" :class="{active: element.showtime}">
                                    <div class="showtime-day" :class="{active: checkDay(day, index)}" v-for="(day) in days" @click="activateDay(day, index)">
                                        {{ day }}
                                    </div>
                                    <div class="showtime-icon" @click="activateShowTime(index)"></div>
                                </div>
                                <div class="edit"></div>
                                <div class="sortable"></div>
                            </div>
                        </div>
                    </template>
                </draggable>
            </perfect-scrollbar>
        </div>
    </div>
  </div>
</template>

